mapper.select:
SELECT crl.id, cd.id AS chat_id, crl.user_comedian_id, crl.user_composer_id, cd.send_user_id, cd.chat_message, comedian.user_name AS comedian_name, composer.user_name AS composer_name, cd.create_at FROM chat_detail cd INNER JOIN ( SELECT max(id) as id FROM chat_detail GROUP BY chat_room_list_id) as _cd on _cd.id = cd.id INNER JOIN chat_room_list crl ON crl.id = cd.chat_room_list_id INNER JOIN user AS comedian on comedian.id = crl.user_comedian_id INNER JOIN user AS composer ON composer.id = crl.user_composer_id WHERE
user_comedian_id = #{userComedianId}
user_composer_id = #{userComposerId}
ORDER BY cd.create_at DESC
