mapper.insert:
UPDATE
    oogiri_theme
SET
    oogiri_theme.update_at = #{now}
WHERE
    oogiri_theme.id = #{themeId}

mapper.update:
UPDATE
    oogiri_answer
SET
    delete_at = #{now}
WHERE id = #{answerId}

UPDATE
    oogiri_reaction
SET
    reaction_status = #{reactionStatus},
    update_at = #{now}
WHERE id = #{reactionId}
